const paymentMethods = {
  transferencia_bancaria: 'Transferencia Bancaria',
  billetera_digital: 'Billetera Digital',
  efectivo: 'Efectivo',
  tarjeta_credito: 'Tarjeta de Cr√©dito',
  tarjeta_debito: 'Tarjeta de D√©bito'
};

const UserP2POffers = ({ wallet, onCreateOffer }) => {
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (wallet) {
      loadUserOffers();
    }
  }, [wallet]);

  const loadUserOffers = async () => {
    try {
      setLoading(true);
      const response = await p2pService.getUserP2POffers(wallet);
      setOffers(response.data.data || []);
    } catch (error) {
      console.error('Error loading user offers:', error);
      setOffers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (offer) => {
    try {
      const newStatus = offer.status === 'activa' ? 'pausada' : 'activa';
      const response = await p2pService.updateOfferStatus(offer.id, wallet, newStatus);

      // Actualizar la oferta localmente
      setOffers(prevOffers => 
        prevOffers.map(o => 
          o.id === offer.id ? { ...o, status: newStatus } : o
        )
      );

      // Si estamos en modo offline, mostrar mensaje
      if (response && response._offline) {
        alert('Cambio guardado en modo offline. Se sincronizar√° cuando el servidor est√© disponible.');
      }
    } catch (error) {
      console.error('Error updating offer status:', error);

      if (error.message === 'Network Error' || error.code === 'ERR_NETWORK') {
        alert('Error de conexi√≥n al servidor. El cambio se guardar√° localmente y se sincronizar√° cuando el servidor est√© disponible.');

        // Actualizar la oferta localmente de todos modos
        const newStatus = offer.status === 'activa' ? 'pausada' : 'activa';
        setOffers(prevOffers => 
          prevOffers.map(o => 
            o.id === offer.id ? { ...o, status: newStatus, _offline: true } : o
          )
        );
      } else {
        alert('Error al actualizar el estado de la oferta: ' + (error.response?.data?.error || error.message));
      }
    }
  };

  const handleDeleteOffer = async (offer) => {
    // Usar window.confirm para evitar problemas con ESLint
    if (!window.confirm('¬øEst√°s seguro de que deseas eliminar esta oferta?')) {
      return;
    }

    try {
      const response = await p2pService.updateOfferStatus(offer.id, wallet, 'cancelada');

      // Eliminar la oferta de la lista
      setOffers(prevOffers => prevOffers.filter(o => o.id !== offer.id));

      // Si estamos en modo offline, mostrar mensaje
      if (response && response._offline) {
        alert('Oferta eliminada en modo offline. Se sincronizar√° cuando el servidor est√© disponible.');
      }
    } catch (error) {
      console.error('Error deleting offer:', error);

      if (error.message === 'Network Error' || error.code === 'ERR_NETWORK') {
        alert('Error de conexi√≥n al servidor. La oferta se eliminar√° localmente y se sincronizar√° cuando el servidor est√© disponible.');

        // Eliminar la oferta de la lista de todos modos
        setOffers(prevOffers => prevOffers.filter(o => o.id !== offer.id));
      } else {
        alert('Error al eliminar la oferta: ' + (error.response?.data?.error || error.message));
      }
    }
  };

  if (loading) {
    return <LoadingState>Cargando tus ofertas...</LoadingState>;
  }

  if (offers.length === 0) {
    return (
      <EmptyState>
        <h3>No tienes ofertas creadas</h3>
        <p>Crea tu primera oferta para empezar a tradear</p>
        <CreateOfferButton onClick={onCreateOffer}>
          + Crear Nueva Oferta
        </CreateOfferButton>
      </EmptyState>
    );
  }

  return (
    <OffersContainer>
      {offers.map(offer => (
        <OfferCard
          key={offer.id}
          type={offer.tipo}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <OfferHeader>
            <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
              <OfferType type={offer.tipo}>
                {offer.tipo === 'compra' ? 'Compra' : 'Venta'} USDT
              </OfferType>
              <OfferStatus status={offer.status}>
                {offer.status}
              </OfferStatus>
              {offer._offline && (
                <span style={{ 
                  fontSize: '0.75rem', 
                  color: 'var(--color-warning)',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.25rem'
                }}>
                  <span>‚ö†Ô∏è</span> Pendiente de sincronizar
                </span>
              )}
            </div>
            <OfferPrice>
              {offer.precio_usdt} <span style={{ fontSize: '0.875rem', fontWeight: 'normal' }}>{offer.moneda_local}</span>
            </OfferPrice>
          </OfferHeader>

          <OfferDetails>
            <OfferDetail>
              <DetailLabel>L√≠mites</DetailLabel>
              <DetailValue>
                <span style={{ color: 'var(--color-primary)' }}>{offer.cantidad_min} - {offer.cantidad_max}</span> USDT
              </DetailValue>
            </OfferDetail>
            <OfferDetail>
              <DetailLabel>M√©todo de Pago</DetailLabel>
              <DetailValue>
                {offer.metodo_pago === 'transferencia_bancaria' && <span>üè¶</span>}
                {offer.metodo_pago === 'billetera_digital' && <span>üì±</span>}
                {offer.metodo_pago === 'efectivo' && <span>üíµ</span>}
                {offer.metodo_pago === 'tarjeta_credito' && <span>üí≥</span>}
                {offer.metodo_pago === 'tarjeta_debito' && <span>üí≥</span>}
                {' '}{paymentMethods[offer.metodo_pago] || offer.metodo_pago}
              </DetailValue>
            </OfferDetail>
            <OfferDetail>
              <DetailLabel>Pa√≠s</DetailLabel>
              <DetailValue>
                {offer.pais_codigo === 'CO' && 'üá®üá¥'}
                {offer.pais_codigo === 'MX' && 'üá≤üáΩ'}
                {offer.pais_codigo === 'AR' && 'üá¶üá∑'}
                {offer.pais_codigo === 'VE' && 'üáªüá™'}
                {offer.pais_codigo === 'PE' && 'üáµüá™'}
                {offer.pais_codigo === 'CL' && 'üá®üá±'}
                {offer.pais_codigo === 'BR' && 'üáßüá∑'}
                {offer.pais_codigo === 'ES' && 'üá™üá∏'}
                {offer.pais_codigo === 'US' && 'üá∫üá∏'}
                {' '}{offer.pais_codigo}
              </DetailValue>
            </OfferDetail>
            <OfferDetail>
              <DetailLabel>Tiempo L√≠mite</DetailLabel>
              <DetailValue>
                <span style={{ color: 'var(--color-warning)' }}>‚è±Ô∏è {offer.tiempo_limite}</span> min
              </DetailValue>
            </OfferDetail>
          </OfferDetails>

          <OfferActions>
            {offer.status !== 'cancelada' && offer.status !== 'completada' && (
              <>
                <Button 
                  size="small" 
                  variant={offer.status === 'activa' ? 'warning' : 'success'}
                  onClick={() => handleToggleStatus(offer)}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem',
                    borderRadius: '6px',
                    fontWeight: '600'
                  }}
                >
                  {offer.status === 'activa' ? (
                    <>
                      <span>‚è∏Ô∏è</span> Pausar
                    </>
                  ) : (
                    <>
                      <span>‚ñ∂Ô∏è</span> Activar
                    </>
                  )}
                </Button>
                <Button 
                  size="small" 
                  variant="danger"
                  onClick={() => handleDeleteOffer(offer)}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem',
                    borderRadius: '6px',
                    fontWeight: '600'
                  }}
                >
                  <span>üóëÔ∏è</span> Eliminar
                </Button>
              </>
            )}
          </OfferActions>
        </OfferCard>
      ))}

      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        marginTop: '2rem' 
      }}>
        <CreateOfferButton onClick={onCreateOffer}>
          + Crear Nueva Oferta
        </CreateOfferButton>
      </div>
    </OffersContainer>
  );
};

export default UserP2POffers;
